import React from "react";
import RecipeList from "./RecipeList";
import "../css/app.css";

const App = () => {
  return <RecipeList recipes={sampleRecipes} />;
};

const sampleRecipes = [
  {
    id: 1,
    name: "Plain Chicken",
    servings: 4,
    cookTime: "1:45",
    instructions:
      "1. Put salt on chicken \n2. Put chicken in oven \n3. Eat chicken",
    ingredients: [
      {
        id: 1,
        name: "Chicken",
        amount: "2 Pounds",
      },
      {
        id: 2,
        name: "Salt",
        amount: "1 Table spoon",
      },
    ],
  },
  {
    id: 2,
    name: "Plain fish",
    servings: 5,
    cookTime: "0:50",
    instructions: "1. Put salt on fish \n2. Put fish in oven \n3. Eat fish",
    ingredients: [
      {
        id: 1,
        name: "Fish",
        amount: "1 Pound",
      },
      {
        id: 2,
        name: "Salt",
        amount: "A Half Table spoon",
      },
    ],
  },
];

export default App;
