import React from "react";

export default function Counter() {
  return (
    <div>
      <button onClick={() => this.handleChange(-1)}>-</button>
      <p>{this.state.count}</p>
      <button onClick={() => this.handleChange(1)}>+</button>
    </div>
  );
}
