import React, { useState } from "react";

export default function Counter({ initialValue }) {
  const [count, setCount] = useState(initialValue);

  return (
    <div>
      <button onClick={() => setCount((prevState) => prevState - 1)}>-</button>
      <p>{count}</p>
      <button onClick={() => setCount((prevState) => prevState + 1)}>+</button>
    </div>
  );
}

