import React, { useState } from "react";

export default function Counter({ initialValue }) {
  const [count, setCount] = useState({ count: initialValue });
  return (
    <div>
      <button onClick={() => setCount((prevState) => prevState.count - 1)}>
        -
      </button>
      <p>{count.count}</p>
      <button onClick={() => setCount({ prevState: prevState.count + 1 })}>
        +
      </button>
    </div>
  );
}
