import React, { Component } from "react";

export default class Counter extends Component {
  state = { count: this.props.initialValue };

  render() {
    return (
      <div>
        <button onClick={() => this.handleClick(-1)}>-</button>
        <p>{this.state.count}</p>
        <button>+</button>
      </div>
    );
  }
}
